/**
 * @param {ICursorEvent} event
 */
export function cursorDown(event) {
	// Mario is on the ground and should jump now
	if (currentDino.walk.visible) {
		thisScene.getLayer(jumpSoundLayerName).play();
		jumpVelocity = 700;
		// Hide the walk sprite, show the jump sprite
		currentDino.walk.visible = false;
		currentDino.jump.visible = true;
	}
}

/**
 * @param {Boolean} value
 */
export function update() {
	//animateChanges();

	// Only read this property once here
	let dt = engine.frametime;

	// Make all coins move
	coins.forEach((coin) => {
		var origin = coin.origin;
		origin.x -= dt * 50;

		// Check distance to mario for pickup
		let distanceMarioSqr = marioOrigin.subtract(origin).lengthSqr();
		let marioCollected = distanceMarioSqr < 400;
		if (marioCollected) {
			// Play the pickup coin sound
			thisScene.getLayer(coinSoundLayerName).play();

			// Remove the old coin particles
			if (coinFx) {
				thisScene.destroyLayer(coinFx);
			}

			// Create new coin particles
			coinFx = thisScene.createLayer(coinAssetFx);
			coinFx.origin = origin;

			++coinCount;
			labelCoins.text = fnFormatScore(coinCount);

			if (coinCount > lastTop) {
				lastTop = coinCount;
				labelTop.text = fnFormatHighscore(lastTop);
				localStorage.set('highscore', coinCount, localStorage.LOCATION_GLOBAL);
			}
		}

		// Coin went out of bounds, delete it
		if (origin.x < -10 || marioCollected) {
			thisScene.destroyLayer(coin);
			coins.splice(coins.indexOf(coin), 1);
		} else {
			// Coin still visible, just move it
			coin.origin = origin;
		}
	});

	// Create new coins
	coinTimer -= dt;
	if (coinTimer < 0.0) {
		coinTimer = 5.0;

		// Timer ran out, create a coin
		let newCoin = thisScene.createLayer(coinAsset);
		thisScene.sortLayer(newCoin, postProcessIndex);
		let origin = new Vec3(360, 90 + 30 * Math.random(), 0);
		newCoin.origin = origin;

		coins.push(newCoin);
	}

	// Mario is moving upwards
	if (jumpVelocity > 50.0) {
		marioOrigin.y += jumpVelocity * dt;
		// Apply air friction
		jumpVelocity -= jumpVelocity * dt;
	}

	let isInAir = marioOrigin.y > initialPosition.y + 1;
	if (isInAir) {
		// Apply gravity
		marioOrigin.y -= 350.0 * dt;
		if (marioOrigin.y < initialPosition.y) {
			// Mario landed
			marioOrigin.y = initialPosition.y;

			// Show the walk sprite, hide the jump sprite
			currentDino.walk.visible = true;
			currentDino.jump.visible = false;
			jumpVelocity = 0;
		}
	}

	// Check horizontal distance to cursor
	let mouseDelta = (shouldFollow ? input.cursorWorldPosition.x : initialPosition.x) - marioOrigin.x;
	let absMouseDelta = Math.abs(mouseDelta);
	if (absMouseDelta > 1) {
		// Move mario towards the cursor
		let mouseSign = Math.sign(mouseDelta);
		marioOrigin.x += Math.min(isInAir ? 30 : 10, absMouseDelta) * dt * mouseSign;
	}

	// Update the mario walk and jump sprite
	currentDino.jump.origin = marioOrigin;
	currentDino.walk.origin = marioOrigin;
	thisLayer.origin = marioOrigin;
}

/**
 * @param {Boolean} value
 */
export function init(value) {
	initialPosition = thisLayer.origin;
	marioOrigin = thisLayer.origin;
	postProcessIndex = thisScene.getLayerIndex('postprocess');

	labelCoins = thisScene.getLayer('label_coins');
	labelTop = thisScene.getLayer('label_top');
	lastTop = localStorage.get('highscore', localStorage.LOCATION_GLOBAL) || 0;
	labelTop.text = fnFormatHighscore(lastTop);

	dinos.vita = {
		walk: thisLayer,
		jump: thisScene.getLayer('vita_jump')
	};
	dinos.doux = {
		walk: thisScene.getLayer('doux_walk'),
		jump: thisScene.getLayer('doux_jump')
	};
	dinos.mort = {
		walk: thisScene.getLayer('mort_walk'),
		jump: thisScene.getLayer('mort_jump')
	};
	dinos.tard = {
		walk: thisScene.getLayer('tard_walk'),
		jump: thisScene.getLayer('tard_jump')
	};

	currentDino = dinos.vita;
	// console.log('init');

	//jumpLayer = thisScene.getLayer('mario_jump');
	//jumpLayer.origin = marioOrigin;
	//jumpLayer.visible = false;
	// applyUserProperties(engine.userProperties);

	backgrounds.mario = backgrounds.mario.map(fnConvertLayers);
	backgrounds.industrial = backgrounds.industrial.map(fnConvertLayers);
	backgrounds.country = backgrounds.country.map(fnConvertLayers);
	backgrounds.glacier = backgrounds.glacier.map(fnConvertLayers);
	backgrounds.mountains = backgrounds.mountains.map(fnConvertLayers);

	// backgrounds.mario.forEach(fnHideLayers);
	// backgrounds.industrial.forEach(fnHideLayers);
	// backgrounds.country.forEach(fnHideLayers);
	// backgrounds.glacier.forEach(fnHideLayers);
	// backgrounds.mountains.forEach(fnHideLayers);
}

/**
 * @param {Object} userProperties
 */
export function applyUserProperties(userProperties) {
	// console.log(userProperties, userProperties.dino);
	if (userProperties.dino) {
		currentDino.walk.visible = false;
		currentDino.jump.visible = false;
		currentDino = dinos[userProperties.dino] || dinos.vita;
		currentDino.walk.visible = true;
		currentDino.jump.visible = false;
	}

	if (userProperties.level) {
		backgrounds.mario.forEach(fnHideLayers);
		backgrounds.industrial.forEach(fnHideLayers);
		backgrounds.country.forEach(fnHideLayers);
		backgrounds.glacier.forEach(fnHideLayers);
		backgrounds.mountains.forEach(fnHideLayers);

		backgrounds[userProperties.level].forEach(fnShowLayers);
	}

	if (typeof userProperties.follow_cursor !== 'undefined') {
		shouldFollow = userProperties.follow_cursor;
	}
}

'use strict';

/**
 * @param {Object} userProperties
 */
export function applyUserProperties(userProperties) {
	if (userProperties.level) {
		let matDownsample = thisObject.getMaterial(0);
		let matCast = thisObject.getMaterial(1);
		switch (userProperties.level) {
			case 'mountains':
				matDownsample.raythreshold = 0.52;
				matCast.rayintensity = 0.4;
				break;
			case 'industrial':
				matDownsample.raythreshold = 0.31;
				matCast.rayintensity = 0.6;
				break;
			case 'glacier':
			case 'country':
				matDownsample.raythreshold = 0.79;
				matCast.rayintensity = 0.6;
				break;
			case 'mario':
				matDownsample.raythreshold = 0.78;
				matCast.rayintensity = 0.3;
				break;
		}
	}
}
